mapper.select:
SELECT pat_id AS patID, nur_id AS nurID, doc_id AS docID, pat_pass AS patPass, pat_name AS patName, pat_gen AS patGen, pat_jumin AS patJumin, pat_addr AS patAddr, pat_phone AS patPhone, pat_email AS patEmail, pat_job AS patJob FROM Patient WHERE state = ‘ACTIVE’
AND title like #{title}
AND author_name like #{author.name}

SELECT pat_id AS patID, nur_id AS nurID, doc_id AS docID, pat_pass AS patPass, pat_name AS patName, pat_gen AS patGen, pat_jumin AS patJumin, pat_addr AS patAddr, pat_phone AS patPhone, pat_email AS patEmail, pat_job AS patJob FROM Patient WHERE state = ‘ACTIVE’
AND title like #{title}
AND author_name like #{author.name}
AND featured = 1

SELECT pat_id AS patID, nur_id AS nurID, doc_id AS docID, pat_pass AS patPass, pat_name AS patName, pat_gen AS patGen, pat_jumin AS patJumin, pat_addr AS patAddr, pat_phone AS patPhone, pat_email AS patEmail, pat_job AS patJob FROM Patient WHERE
state = #{state}
AND title like #{title}
AND author_name like #{author.name}

SELECT pat_id AS patID, nur_id AS nurID, doc_id AS docID, pat_pass AS patPass, pat_name AS patName, pat_gen AS patGen, pat_jumin AS patJumin, pat_addr AS patAddr, pat_phone AS patPhone, pat_email AS patEmail, pat_job AS patJob FROM Patient
state = #{state}
AND title like #{title}
AND author_name like #{author.name}

mapper.update:
update Author

    username=#{username},
    password=#{password},
    email=#{email},
    bio=#{bio}

where id=#{id}
